    class Solution {
        public long subArrayRanges(int[] nums) {
            int n = nums.length;
            long res = 0;
            int[] add = new int[n];
            int[] down = new int[n];
            for (int i = 0; i < n; i++) {
                long min = Long.MAX_VALUE, max = Long.MIN_VALUE;
                for (int j = i; j < n; j++) {
                    min = Math.min(min, nums[j]);
                    max = Math.max(max, nums[j]);

                    long diff = (int) (max - min);
//                    System.out.println(max + " " + min + " >> " + diff);
                    res += diff;
                }
            }
            return res;
        }
    }
